local nX,nY = term.getCursorPos()
local tArgs = { ... }
local bPartTwo = (tArgs[1] == "2")
local nCount = 0


for i=1,#sInput do
    --[[ c a l c u l a t i o n ]]
    if sInput:sub(i,i) == '(' then
        nCount = nCount+1
    else
        nCount = nCount-1
    end
    if bPartTwo and nCount < 0 then
        nCount = i
        break
    end
    --[[ Print progress ]]
    term.write(i.."/"..#sInput.." "..sInput:sub(i,i).." ")
    term.setCursorPos(nX,nY)
    sleep()
end


term.setCursorPos(nX,nY)
term.write("DONE."..(' '):rep(#tostring(#sInput)*2-2))
term.setCursorPos(nX,nY+1)
print("The end result is: "..nCount.."!")